import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MainNav from './Main_Nav';

const CartPage = () => {
    const [marketplaces, setMarketplaces] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const userId = 1; // Replace with actual user_id logic

    useEffect(() => {
        const fetchCartData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/cartpage/${userId}`);
                setMarketplaces(response.data);
            } catch (err) {
                setError('Failed to fetch cart data.');
            } finally {
                setLoading(false);
            }
        };

        fetchCartData();
    }, [userId]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    const totalItems = marketplaces.reduce(
        (total, marketplace) =>
            total + (marketplace.products ? marketplace.products.reduce((sum, product) => sum + product.quantity, 0) : 0),
        0
    );

    const totalPrice = marketplaces.reduce(
        (total, marketplace) =>
            total + (marketplace.products ? marketplace.products.reduce((sum, product) => sum + product.price * product.quantity, 0) : 0),
        0
    );

    const estimatedTaxes = 200; // Static tax value
    const deliveryFee = 100; // Static delivery fee
    const estimatedTotal = totalPrice + estimatedTaxes + deliveryFee;

    return (
        <>
            <MainNav />
            <div className="cart-page">
                <h1>Your Cart: {totalItems} items</h1>
                <div className="cart-content">
                    <div className="cart-marketplaces">
                        {marketplaces.map((marketplace, index) => (
                            <div className="marketplace" key={index}>
                                <h2>{marketplace.name}</h2>
                                {marketplace.products &&
                                    marketplace.products.map((product, idx) => (
                                        <div className="product" key={idx}>
                                            <img
                                                src={product.image}
                                                alt={product.name}
                                                className="product-image"
                                            />
                                            <div className="product-details">
                                                <span>{product.name}</span>
                                                <span>QTY: {product.quantity}</span>
                                                <span>${product.price.toFixed(2)}</span>
                                            </div>
                                        </div>
                                    ))}
                            </div>
                        ))}
                    </div>

                    <div className="cart-summary">
                        <h3>Subtotal: {totalItems} items</h3>
                        <p>$ {totalPrice.toFixed(2)}</p>
                        <h3>Est. taxes:</h3>
                        <p>$ {estimatedTaxes.toFixed(2)}</p>
                        <hr />
                        <h3>Est. total</h3>
                        <p>with delivery ${deliveryFee.toFixed(2)}</p>
                        <h2>$ {estimatedTotal.toFixed(2)}</h2>
                        <button className="checkout-btn">Checkout</button>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CartPage;
