import ProductList from "./ProductList.js"
import { useLocation } from 'react-router-dom';

function MainPage() { 

  const location = useLocation();
  const user = location.state?.user; // Access user data from navigation state

  if (!user) {
      return <p>No user data available. Please log in or sign up.</p>;
  }  // const { user } = location.state ;
  // console.log(location)
  // console.log("now ", user.username);
    return (
        <>
          
          <MainNav/>
          <div >
          
          
          <div> 
          
            <div>
              <>
              <h1>{user.username}Hello to DealX </h1>
              </>
              <h1>Amazon products</h1>          
            </div> 
            <ProductList />
          </div>
          <div>
            <h1>Walmart products</h1>
            <ProductList/>
          </div>
          <div>

            <h1>Etsy products</h1>
            <ProductList/>
          </div>
          </div>
      
        </>
    );
}

export default MainPage
