import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';
import { useUser } from './UserContext'; // Import the context
import { Link } from 'react-router-dom';

const LoginForm = () => {
    const navigate = useNavigate();
    const { login } = useUser(); // Access login function from context
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // For displaying error messages

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/users', {
                email,
                password_hash: password,
            });
            if (response.status === 200) {
                navigate('/mainUser', { state: { user: response.data } });
            }

            if (response.data.token) {
                // Store the user data in context
                login(response.data);
                
                // Store the token in localStorage for session persistence
                localStorage.setItem('token', response.data.token);
                
                // Redirect the user to the profile page or dashboard
            } else {
                setError('No token received. Please try again.');
            }
        } catch (error) {
            console.error('Login failed:', error);
            setError('Login failed. Please check your credentials.');
        }
    };

return (
    <div className="login-container">
        <form className="login-form" onSubmit={handleSubmit}>
            <h2>Login</h2>
            <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    placeholder="Enter your email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                    type="password"
                    id="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <button type="submit" className="login-button">Login</button>
            <p className="signup-link">
                Don't have an account? <Link to ="/signup">Sign Up</Link>
            </p>
        </form>
    </div>
); 
};


const SignUpForm = () => {
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [address, setAddress] = useState('');
const [username, setUsername] = useState('');
const [firstName, setFirstName] = useState('');
const [lastName, setLastName] = useState('');
const [phoneNumber, setPhoneNumber] = useState('');
const navigate = useNavigate();

const handleSubmitSignUp = async (e) => {
e.preventDefault();

const formData = {
    email,
    password_hash: password,
    address,
    username,
    firstName,
    lastName,
    phoneNumber,
};
try {
    const response = await axios.post('http://localhost:5000/api/users/signup', formData);

    if (response.status === 200) {

        navigate('/mainUser', { state: { user: formData } }); 
        alert("Your Account is Created");
    }
} catch (error) {
    alert("error")
}
console.log('Signup Form Data:', formData);

};

return (
<div className="login-container">
    <form className="login-form" onSubmit={handleSubmitSignUp}>
    <h2>Sign Up</h2>

    <div className="form-group">
        <label htmlFor="username">Username</label>
        <input
        type="text"
        id="username"
        placeholder="Enter your username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="firstName">First Name</label>
        <input
        type="text"
        id="firstName"
        placeholder="Enter your first name"
        value={firstName}
        onChange={(e) => setFirstName(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="lastName">Last Name</label>
        <input
        type="text"
        id="lastName"
        placeholder="Enter your last name"
        value={lastName}
        onChange={(e) => setLastName(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="email">Email</label>
        <input
        type="email"
        id="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="password">Password</label>
        <input
        type="password"
        id="password"
        placeholder="Enter your password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="address">Address</label>
        <input
        type="text"
        id="address"
        placeholder="Enter your address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        required
        />
    </div>

    <div className="form-group">
        <label htmlFor="phoneNumber">Phone Number</label>
        <input
        type="tel"
        id="phoneNumber"
        placeholder="Enter your phone number"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
        required
        />
    </div>

    <button type="submit" className="login-button">Sign Up</button>
    </form>
</div>
);
};

export default {LoginForm,SignUpForm};
