import React, { useState } from 'react';
import axios from 'axios';
import Card from './Card';

const FilteredProductsPage = () => {
const [products, setProducts] = useState([]); // Holds the list of products
const [searchQuery, setSearchQuery] = useState(''); // Holds the search query
const [errorMessage, setErrorMessage] = useState(''); // Holds any error message for display
const [isLoading, setIsLoading] = useState(false); // Tracks loading state

// Fetch filtered products based on the search query
const fetchFilteredProducts = async () => {
setIsLoading(true); // Set loading to true before the API call
setErrorMessage(''); // Clear any previous error messages

try {
    const response = await axios.get('http://localhost:5000/api/products/search', {
    params: { search: searchQuery },
    });

    // Check for empty products and handle accordingly
    if (response.data.products && response.data.products.length > 0) {
    setProducts(response.data.products);
    } else {
    setProducts([]); // Clear products if none are found
    setErrorMessage('No products found.');
    }
} catch (error) {
    console.error('Error fetching filtered products:', error);

    // Set user-friendly error message
    setErrorMessage('Failed to fetch products. Please try again later.');
} finally {
    setIsLoading(false); // Reset loading state after API call
}
};

// Handle changes in the search input field
const handleSearchChange = (event) => {
setSearchQuery(event.target.value); // Update search query state
};

// Handle the search form submission
const handleSearchSubmit = (event) => {
event.preventDefault(); // Prevent default form submission behavior
if (searchQuery.trim() === '') {
    setErrorMessage('Please enter a search query.');
    return;
}
fetchFilteredProducts(); // Fetch products for the entered query
};

return (
<div className="product-list">
    <h1>Search Products</h1>

    {/* Search Form */}
    <form onSubmit={handleSearchSubmit}>
    <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search for products..."
        aria-label="Search Products"
    />
    <button type="submit" disabled={isLoading}>
        {isLoading ? 'Searching...' : 'Search'}
    </button>
    </form>

    {/* Error Message */}
    {errorMessage && <p className="error-message">{errorMessage}</p>}

    {/* Product List */}
    {isLoading ? (
    <p>Loading...</p>
    ) : products.length > 0 ? (
    products.map((product) => <Card key={product.id} product={product} />)
    ) : (
    !errorMessage && <p>No products found.</p>
    )}
</div>
);
};

export default FilteredProductsPage;
