import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MainNav from './Main_Nav';
import {useUser} from './UserContext' ; 

import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';


const CartPage = () => {
    const [products, setProducts] = useState([]); // Initialize as an empty array
    const [error, setError] = useState(null);
    const {user} = useUser();
    console.log(user)
    const userId = user.data.user_id; 
    
const stripePromise = loadStripe('your-publishable-key-here');




const [loading, setLoading] = useState(false);
const stripe = useStripe();
const elements = useElements();

const handleSubmit = async (event) => {
event.preventDefault();
if (!stripe || !elements) return;

setLoading(true);

const { error, paymentMethod } = await stripe.createPaymentMethod({
type: 'card',
card: elements.getElement(CardElement),
});

if (error) {
console.log('[Error]', error);
} else {
// Send the payment method to your server for further processing
const res = await fetch('/create-payment-intent', {
    method: 'POST',
    body: JSON.stringify({
    payment_method: paymentMethod.id,
    }),
});
const paymentIntent = await res.json();

const { error: confirmError } = await stripe.confirmCardPayment(paymentIntent.client_secret);

if (confirmError) {
    console.log('[Error]', confirmError);
} else {
    console.log('Payment Successful!');
}
}

setLoading(false);
};
    // Replace with actual user_id logic
    useEffect(() => {
        const fetchCartData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/cartpage/${userId}`);
                console.log('API Response:', response.data);
                // Ensure the response is an array of items
                setProducts(response.data.items || []); // Safely set products
            } catch (err) {
                console.error('Error fetching cart data:', err);
                setError('Failed to fetch cart data.');
            } finally {
                setLoading(false);
            }
        };

        fetchCartData();
    }, [userId]);

    // Debugging the `products` state
    useEffect(() => {
        console.log("Updated products state:", products);
    }, [products]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    // Guard against empty or malformed products array
    const totalItems = products.reduce((sum, product) => sum + (product.quantity || 0), 0);
    const totalPrice = products.reduce((sum, product) => sum + (product.price || 0) * (product.quantity || 0), 0);

    const estimatedTaxes = 200; // Static tax value
    const deliveryFee = 100; // Static delivery fee
    const estimatedTotal = totalPrice + estimatedTaxes + deliveryFee;

    return (
        <>
            <MainNav />
            <div className="cart-page">
                <h1>Your Cart: {totalItems} items</h1>
                <div className="cart-content">
                    <div className="cart-products">
                        {products.map((product, index) => (
                            <div className="product" key={index}>
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="product-image"
                                />
                                <div className="product-details">
                                    <span>{product.name}</span>
                                    <span>QTY: {product.quantity}</span>
                                    <span>${(product.price || 0)}</span>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="cart-summary">
                        <h3>Subtotal: {totalItems} items</h3>
                        <p>$ {totalPrice}</p>
                        <h3>Est. taxes:</h3>
                        <p>$ {estimatedTaxes}</p>
                        <hr />
                        <h3>Est. total</h3>
                        <p>with delivery ${deliveryFee}</p>
                        <h2>$ {estimatedTotal}</h2>
                        <form onSubmit={handleSubmit}>
                            <CardElement />
                            <button className = "checkout-btn" type="submit" disabled={!stripe || loading}>Checkout</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CartPage;
